/*
CSS for the HTML keylog table
*/

#define TABLE_CSS "<style>.table {table-layout: fixed; margin:0px;padding:0px; width:100%; border:1px solid #000000; -moz-border-radius-bottomleft:0px; -webkit-border-bottom-left-radius:0px; border-bottom-left-radius:0px; -moz-border-radius-bottomright:0px; -webkit-border-bottom-right-radius:0px; border-bottom-right-radius:0px; -moz-border-radius-topright:0px; -webkit-border-top-right-radius:0px; border-top-right-radius:0px; -moz-border-radius-topleft:0px; -webkit-border-top-left-radius:0px; border-top-left-radius:0px; }.table table{ border-collapse: collapse; border-spacing: 0; width:100%; height:100%; margin:0px;padding:0px; }.table tr:last-child td:last-child { -moz-border-radius-bottomright:0px; -webkit-border-bottom-right-radius:0px; border-bottom-right-radius:0px; } .table table tr:first-child td:first-child { -moz-border-radius-topleft:0px; -webkit-border-top-left-radius:0px; border-top-left-radius:0px; } .table table tr:first-child td:last-child { -moz-border-radius-topright:0px; -webkit-border-top-right-radius:0px; border-top-right-radius:0px; }.table tr:last-child td:first-child{ -moz-border-radius-bottomleft:0px; -webkit-border-bottom-left-radius:0px; border-bottom-left-radius:0px; }.table tr:hover td{ } .table tr:nth-child(odd){ background-color:#e5e5e5; } .table tr:nth-child(even) { background-color:#ffffff; }.table td{ vertical-align:middle; border:1px solid #000000; border-width:0px 1px 1px 0px; text-align:center; padding:14px; font-size:14px; font-family:Arial; font-weight:normal; color:#000000; }.table tr:last-child td{ border-width:0px 1px 0px 0px; }.table tr td:last-child{ border-width:0px 0px 1px 0px; }.table tr:last-child td:last-child{ border-width:0px 0px 0px 0px; } .table tr:first-child td{ background:-o-linear-gradient(bottom, #5656ff 5%, #00007f 100%); background:-webkit-gradient( linear, left top, left bottom, color-stop(0.05, #5656ff), color-stop(1, #00007f) ); background:-moz-linear-gradient( center top, #5656ff 5%, #00007f 100% ); filter:progid:DXImageTransform.Microsoft.gradient(startColorstr=\"#5656ff\", endColorstr=\"#00007f\"); background: -o-linear-gradient(top,#5656ff,00007f); background-color:#5656ff; border:0px solid #000000; text-align:center; border-width:0px 0px 1px 1px; font-size:20px; font-family:Arial; font-weight:bold; color:#ffffff; } .table tr:first-child:hover td{ background:-o-linear-gradient(bottom, #5656ff 5%, #00007f 100%); background:-webkit-gradient( linear, left top, left bottom, color-stop(0.05, #5656ff), color-stop(1, #00007f) ); background:-moz-linear-gradient( center top, #5656ff 5%, #00007f 100% ); filter:progid:DXImageTransform.Microsoft.gradient(startColorstr=\"#5656ff\", endColorstr=\"#00007f\"); background: -o-linear-gradient(top,#5656ff,00007f); background-color:#5656ff; } .table tr:first-child td:first-child{ border-width:0px 0px 1px 0px; } .table tr:first-child td:last-child{ border-width:0px 0px 1px 1px; }</style>";
